(()=>{"use strict";class t{static getWeatherLink=t=>{const e=`https://api.openweathermap.org/data/2.5/weather?q=${t}&APPID=8b09689c50ce1e845011934fc53575bd`;this.fetchLink(e)};static fetchLink=async t=>{const e=await fetch(t,{mode:"cors"}),n=await e.json();this.usableData(n)};static usableData=t=>{const e=this.convertKelvin(t.main.temp),n=`${t.name}, ${t.sys.country}`,i=this.convertKelvin(t.main.feels_like),s=`${t.main.humidity.toString()} %`,c=t.weather[0].description,a=t.weather[0].main,o=`${this.convertSpeed(t.wind.speed).toFixed(1)} km/h`;return console.table({temp:e,place:n,feelsLike:i,humidityPercent:s,longDesc:c,shortDesc:a,wind:o})};static convertKelvin=t=>{const e=`${(t-273.15).toFixed(0)} 째C`;return(1.8*(t-273.15)+32).toFixed(0),e};static convertSpeed=t=>3.6*t}(class{static initButtons=()=>{const t=document.getElementById("search-btn"),e=document.getElementById("change-unit-btn");t.addEventListener("click",this.getLocation),e.addEventListener("click",this.toggleUnits)};static getLocation=()=>{const e=document.getElementById("search-input").value;""!==e&&t.getWeatherLink(e)};static toggleUnits=()=>{const t=document.getElementById("change-unit-btn");"Units to 째F"===t.textContent?t.textContent="Units to 째C":t.textContent="Units to 째F"}}).initButtons()})();